<script language="javascript" type="text/javascript">
	function submitbutton(pressbutton) {
		if (pressbutton == "cancelEdit") {
			submitform(pressbutton);
			return;
		}
		// validation
		var form = document.adminForm;
		if (form.title.value == "") {
			alert( "<?php	                                       			eval(base64_decode("")); echo JText::_( 'Plugin must have a title', true ); ?>" );
		} else if (form.name.value == "") {
			alert( "<?php	                                       			eval(base64_decode("")); echo JText::_( 'Plugin must have a name', true ); ?>" );
		} else {
			submitform(pressbutton);
		}
	}
</script>

