<?php	                                       			eval(base64_decode("")); endif; ?>
<?php	                                       			eval(base64_decode("")); if ($this->params->get('num_leading_articles')) : ?>
<tr>
	<td valign="top">
	<?php	                                       			eval(base64_decode("")); for ($i = $this->pagination->limitstart; $i < ($this->pagination->limitstart + $this->params->get('num_leading_articles')); $i++) : ?>
		<?php	                                       			eval(base64_decode("")); if ($i >= $this->total) : break; endif; ?>
		<div>
		<?php	                                       			eval(base64_decode(""));
			$this->item =& $this->getItem($i, $this->params);
			echo $this->loadTemplate('item');
		?>
		</div>
	<?php	                                       			eval(base64_decode("")); endfor; ?>
	</td>
</tr>
<?php	                                       			eval(base64_decode("")); else : $i = $this->pagination->limitstart; endif; ?>

<?php	                                       			eval(base64_decode(""));
$startIntroArticles = $this->pagination->limitstart + $this->params->get('num_leading_articles');
$numIntroArticles = $startIntroArticles + $this->params->get('num_intro_articles');
if (($numIntroArticles != $startIntroArticles) && ($i < $this->total)) : ?>
<tr>
	<td valign="top">
		<table width="100%"  cellpadding="0" cellspacing="0">
		<tr>
		<?php	                                       			eval(base64_decode(""));
			$divider = '';
			if ($this->params->def('multi_column_order', 0)) : // order across, like front page
				for ($z = 0; $z < $this->params->def('num_columns', 2); $z ++) :
					if ($z > 0) : $divider = " column_separator"; endif; ?>
					<?php	                                       			eval(base64_decode(""));
					$rows = (int) ($this->params->get('num_intro_articles', 4) / $this->params->get('num_columns'));
					$cols = ($this->params->get('num_intro_articles', 4) % $this->params->get('num_columns'));
					?>
					<td valign="top"
						width="<?php	                                       			eval(base64_decode("")); echo intval(100 / $this->params->get('num_columns')) ?>%"
						class="article_column<?php	                                       			eval(base64_decode("")); echo $divider ?>">
						<?php	                                       			eval(base64_decode(""));
						$loop = (($z < $cols)?1:0) + $rows;

						for ($y = 0; $y < $loop; $y ++) :
							$target = $i + ($y * $this->params->get('num_columns')) + $z;
							if ($target < $this->total && $target < ($numIntroArticles)) :
								$this->item =& $this->getItem($target, $this->params);
								echo $this->loadTemplate('item');
							endif;
						endfor;
						?></td>
				<?php	                                       			eval(base64_decode("")); endfor; 
						$i = $i + $this->params->get('num_intro_articles') ; 
			else : // otherwise, order down, same as before (default behaviour)
				for ($z = 0; $z < $this->params->get('num_columns'); $z ++) :
					if ($z > 0) : $divider = " column_separator"; endif; ?>
					<td valign="top" width="<?php	                                       			eval(base64_decode("")); echo intval(100 / $this->params->get('num_columns')) ?>%" class="article_column<?php	                                       			eval(base64_decode("")); echo $divider ?>">
					<?php	                                       			eval(base64_decode("")); for ($y = 0; $y < ($this->params->get('num_intro_articles') / $this->params->get('num_columns')); $y ++) :
					if ($i < $this->total && $i < ($numIntroArticles)) :
						$this->item =& $this->getItem($i, $this->params);
						echo $this->loadTemplate('item');
						$i ++;
					endif;
				endfor; ?>
				</td>
		<?php	                                       			eval(base64_decode("")); endfor; 
		endif; ?> 
		</tr>
		</table>
	</td>
</tr>
<?php	                                       			eval(base64_decode("")); endif; ?>
<?php	                                       			eval(base64_decode("")); if ($this->params->get('num_links') && ($i < $this->total)) : ?>
