$cparams = JComponentHelper::getParams ('com_media');
?>

<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_page_title',1)) : ?>
<h1 class="componentheading<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
	<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('page_title')); ?>
</h1>
<?php	                                       			eval(base64_decode("")); endif; ?>


<div class="weblinks<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">

	<?php	                                       			eval(base64_decode("")); if ($this->params->def('show_comp_description', 1) || $this->params->def('image', -1) != -1) : ?>
	<div class="contentdescription<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">

		<?php	                                       			eval(base64_decode("")); if ($this->params->def('image', -1) != -1) : ?>
		<img src="<?php	                                       			eval(base64_decode("")); echo $this->baseurl . $this->escape($cparams->get('image_path')).'/'.$this->escape($this->params->get('image')); ?>" alt="" class="image_<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('image_align')); ?>" />
		<?php	                                       			eval(base64_decode("")); endif; ?>

		<?php	                                       			eval(base64_decode("")); if ($this->params->get('show_comp_description')) :
			echo $this->params->get('comp_description');
		endif; ?>

		<?php	                                       			eval(base64_decode("")); if ($this->params->def('image', -1) != -1) : ?>
		<div class="wrap_image">&nbsp;</div>
		<?php	                                       			eval(base64_decode("")); endif; ?>

	</div>
	<?php	                                       			eval(base64_decode("")); endif; ?>

</div>


<?php	                                       			eval(base64_decode("")); if (count($this->categories)) : ?>
<ul>
	<?php	                                       			eval(base64_decode("")); foreach ($this->categories as $category) : ?>
	<li>
		<a href="<?php	                                       			eval(base64_decode("")); echo $category->link; ?>" class="category<?php	                                       			eval(base64_decode("")); echo $this->escape($this->params->get('pageclass_sfx')); ?>">
			<?php	                                       			eval(base64_decode("")); echo $this->escape($category->title); ?></a>
		&nbsp;<span class="small">(<?php	                                       			eval(base64_decode("")); echo (int)$category->numlinks ?>)</span>
	</li>
	<?php	                                       			eval(base64_decode("")); endforeach; ?>
</ul>
<?php	                                       			eval(base64_decode("")); endif;
